mapper.select:
SELECT a.id id, a.categoryId categoryId, a.title title, a.url url, a.imgUrl imgUrl, a.orderNum orderNum, a.recommendType mediaType FROM recommend a WHERE 1=1
AND a.categoryId = #{categoryId, jdbcType=INTEGER}
AND a.recommendType = #{mediaType, jdbcType=INTEGER}
ORDER BY a.orderNum DESC

SELECT COUNT(*)
  FROM recommend a
 WHERE 1=1

   AND a.categoryId = #{categoryId, jdbcType=INTEGER}


   AND a.recommendType = #{mediaType, jdbcType=INTEGER}
